IF OBJECT_ID ('biorepositories') IS NOT NULL
  DROP TABLE [dbo].[biorepositories]
GO
CREATE TABLE [dbo].[biorepositories]
(
Id int IDENTITY(1,1) PRIMARY KEY,
URL VARCHAR(255) NOT NULL,
NameOfInstitution VARCHAR(255),
IsIndexHerbariorumRecord BIT,
InstitutionalCode VARCHAR(255) NOT NULL,
AdditionalInstitutionNames VARCHAR(255),
UrlForMainInstitutionalWebsite VARCHAR(255),
UrlForInstitutionalSpecimenCatalog VARCHAR(255),
UrlForInstitutionalWebservices VARCHAR(255),
IsActiveStatusOfInstitution BIT,
InstitutionalGovernance VARCHAR(255),
InstitutionType VARCHAR(255),
Country VARCHAR(255)
)
GO

